package pattern;

public class Inter_java_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			Scanner scn=new Scanner(System.in);
		int n=scn.nextInt();
		char ch=65;
		
	
		for(int j=0;j<n;j++) {
			int i=0;
			
		while(i<2*(n-j)) {
			System.out.print(" ");
			i++;
		}
		for(int k=0;k<=j;k++) {
			System.out.print(ch+++" ");
		
		

	}
		System.out.println();
		

}

	}

}
